package com.graphybyte.githubtrendingrepo.coreimport com.google.gson.GsonBuilderimport com.google.gson.annotations.SerializedNameimport okhttp3.ResponseBodyfun parseError(errorBody: ResponseBody?): String {    return try {        errorBody?.string().toString().let {            val data = GsonBuilder().serializeNulls().create().fromJson(it, CommonModel::class.java)            "${data?.message}"        }    } catch (e: Throwable) {        "Something went wrong with that request"    }}data class CommonModel(    @SerializedName("message")    val message: String? = "",    @SerializedName("data")    val data: String? = "",    @SerializedName("status")    val status: Boolean? = false)